<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	public $packages;		public $template_location;		public function __construct()	{				$this->template_location = "Project/".DOMAIN."/Design/Pages/primary/yacht/templates/";	}		public function displayVillasBookTemplate()	{		$content = $this->renderTemplate($this->template_location."yacht_book.phtml");		response::getInstance()->addContentToTree(array("MAIN_CONTENT" => $content));			$content = $this->renderTemplate($this->template_location."inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );			$content = $this->renderTemplate($this->template_location."css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content) );	}		
}?>